package main

import (
	"fmt"
	"strings"
)

func part1() {
	input := ""
	// input := "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
	// input := "nppdvjthqldpwncqszvftbrmjlhg"

	for idx := 4; idx < len(input); idx++ {
		x := input[idx-4 : idx]
		double := false
		for _, sub := range x {
			if strings.Count(x, string(sub)) > 1 {
				double = true
			}
		}
		if !double {
			fmt.Println("Part 1:", idx)
			break
		}
	}
}

